/// \file
/// This file declares the targeting of the Machinelegalizer class for Z80.
/// \todo This should be generated by TableGen.
//===----------------------------------------------------------------------===//

#ifndef LLVM_LIB_TARGET_Z80_Z80MACHINELEGALIZER_H
#define LLVM_LIB_TARGET_Z80_Z80MACHINELEGALIZER_H

#include "llvm/CodeGen/GlobalISel/LegalizerInfo.h"

namespace llvm {

class Z80Subtarget;
class Z80TargetMachine;

/// This class provides the information for the target register banks.
class Z80LegalizerInfo : public LegalizerInfo {
private:
  /// Keep a reference to the Z80Subtarget around so that we can
  /// make the right decision when generating code for different targets.
  const Z80Subtarget &Subtarget;
  const Z80TargetMachine &TM;

public:
  Z80LegalizerInfo(const Z80Subtarget &STI, const Z80TargetMachine &TM);

  LegalizerHelper::LegalizeResult
  legalizeCustomMaybeLegal(LegalizerHelper &Helper,
                           MachineInstr &MI) const override;

  bool legalizeIntrinsic(LegalizerHelper &Helper,
                         MachineInstr &MI) const override;

private:
  LegalizerHelper::LegalizeResult legalizeBitwise(LegalizerHelper &Helper,
                                                  MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeExtractInsert(LegalizerHelper &Helper,
                                                        MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeFConstant(LegalizerHelper &Helper,
                                                    MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeVAStart(LegalizerHelper &Helper,
                                                  MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeShift(LegalizerHelper &Helper,
                                                MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeFunnelShift(LegalizerHelper &Helper,
                                                      MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeCompare(LegalizerHelper &Helper,
                                                  MachineInstr &MI) const;
  LegalizerHelper::LegalizeResult legalizeMemIntrinsic(LegalizerHelper &Helper,
                                                       MachineInstr &MI) const;
};

} // End namespace llvm

#endif
